import React, { useState } from 'react';

function SearchBar({ onSearch }) {
  const [criteria, setCriteria] = useState({
    collection: 'temperature', // You can set the default collection here
    field: '',
    minThreshold: '',
    maxThreshold: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCriteria({ ...criteria, [name]: value });
  };

  const handleSearch = () => {
    onSearch(criteria);
  };

  return (
    <div>
      <select name="collection" onChange={handleChange} value={criteria.collection}>
        <option value="temperature">Temperature</option>
        <option value="pressure">Pressure</option>
        {/* Add more options for other collections */}
      </select>
      <input
        type="text"
        name="field"
        placeholder="Field"
        value={criteria.field}
        onChange={handleChange}
      />
      <input
        type="text"
        name="minThreshold"
        placeholder="collection name"
        value={criteria.minThreshold}
        onChange={handleChange}
      />
      <input
        type="text"
        name="maxThreshold"
        placeholder="collection name"
        value={criteria.maxThreshold}
        onChange={handleChange}
      />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchBar;
