import React, { useState } from 'react';

function SearchBar({ onSearch }) {
  const [criteria, setCriteria] = useState({
    collection: 'temperature', // Default collection
    field: 'temperature', // Default field
    minThreshold: '',
    maxThreshold: '',
  });

  const handleSearch = () => {
    onSearch(criteria);
  };

  return (
    <div>
      <select
        value={criteria.collection}
        onChange={(e) => setCriteria({ ...criteria, collection: e.target.value })}
      >
        <option value="temperature">Temperature</option>
        <option value="pressure">Pressure</option>
        {/* Add more options for other collections */}
      </select>
      <input
        type="text"
        value={criteria.minThreshold}
        onChange={(e) => setCriteria({ ...criteria, minThreshold: e.target.value })}
        placeholder="Min Threshold"
      />
      <input
        type="text"
        value={criteria.maxThreshold}
        onChange={(e) => setCriteria({ ...criteria, maxThreshold: e.target.value })}
        placeholder="Max Threshold"
      />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchBar;
