import React, { useState } from 'react';

function SearchBar({ onSearch }) {
  const [criteria, setCriteria] = useState({
    collection: 'temperature',
    field: 'temperature',
    minThreshold: 0,
    maxThreshold: 100,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCriteria({
      ...criteria,
      [name]: value,
    });
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Collection"
        name="collection"
        value={criteria.collection}
        onChange={handleInputChange}
      />
      <input
        type="text"
        placeholder="Field"
        name="field"
        value={criteria.field}
        onChange={handleInputChange}
      />
      <input
        type="number"
        placeholder="Min Threshold"
        name="minThreshold"
        value={criteria.minThreshold}
        onChange={handleInputChange}
      />
      <input
        type="number"
        placeholder="Max Threshold"
        name="maxThreshold"
        value={criteria.maxThreshold}
        onChange={handleInputChange}
      />
      <button onClick={() => onSearch(criteria)}>Search</button>
    </div>
  );
}

export default SearchBar;
