import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard() {
  const [data, setData] = useState([]);
  const [temperature, setTemperature] = useState('');
  const [pressure, setPressure] = useState('');

  useEffect(() => {
    fetchData(); // Initial data fetch
  }, []); // An empty dependency array to fetch data once when the component mounts

  const fetchData = () => {
    axios.get(`/api/data?temperature=${temperature}&pressure=${pressure}`)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  const handleFilters = () => {
    fetchData(); // Fetch data when the filters are applied
  };

  return (
    <div>
      <h1>MongoDB Dashboard</h1>
      <div>
        <label>Filter by Temperature:</label>
        <input type="number" value={temperature} onChange={(e) => setTemperature(e.target.value)} />
      </div>
      <div>
        <label>Filter by Pressure:</label>
        <input type="number" value={pressure} onChange={(e) => setPressure(e.target.value)} />
      </div>
      <button onClick={handleFilters}>Apply Filters</button>
      <ul>
        {data.map((item) => (
          <li key={item._id}>{item.fieldName}: {item.value}</li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
